    <script>
        $(document).ready(function () {
            var navbar = $(".navbar");
            var header = $("header");
            var headerHeight = header.outerHeight();

            $(window).scroll(function () {
                if ($(this).scrollTop() > headerHeight) {
                    navbar.addClass("fixed-top");
                } else {
                    navbar.removeClass("fixed-top");
                }
            });
        });

        const showContributedBooksButton = document.getElementById('showContributedBooks');
        const contributedBooks = document.getElementById('contributedBooks');
        const showPendingBooksButton = document.getElementById('showPendingBooks');
        const pendingBooks = document.getElementById('pendingBooks');

        // Add click event listeners to the buttons
        showContributedBooksButton.addEventListener('click', () => {
            if (contributedBooks.style.display === 'none') {
                contributedBooks.style.display = 'block';
                showContributedBooksButton.textContent = 'Hide Contributed Books';
            } else {
                contributedBooks.style.display = 'none';
                showContributedBooksButton.textContent = 'Show Contributed Books';
            }
        });

        showPendingBooksButton.addEventListener('click', () => {
            if (pendingBooks.style.display === 'none') {
                pendingBooks.style.display = 'block';
                showPendingBooksButton.textContent = 'Hide Pending Books';
            } else {
                pendingBooks.style.display = 'none';
                showPendingBooksButton.textContent = 'Show Pending Books';
            }
        });
    </script>

